import { useEffect, useState } from "react";
import axios from 'axios';
import BootstrapTable from 'react-bootstrap-table-next';
import paginationFactory from 'react-bootstrap-table2-paginator';
import filterFactory, { textFilter } from 'react-bootstrap-table2-filter';


function Klijent(){

    const [data, setData] = useState(null);

    /*const header = [
        { title: 'OIB', prop: 'OIB' },
        { title: 'Ime', prop: 'Ime' },
        { title: 'Prezime', prop: 'Prezime' },
        { title: 'Adresa', prop: 'Adresa' },
        { title: 'Telefon', prop: 'Telefon' },
        { title: 'Spol', prop: 'Spol' },
      ];*/
      const body = Array.from(new Array(57), () => {
        const rd = (Math.random() * 10).toFixed(1);
      
        if (rd > 0.5) {
          return {
            username: 'i-am-billy',
            realname: `Billy ${rd}`,
            location: 'Mars'
          };
        }
      
        return {
          username: 'john-nhoj',
          realname: `John ${rd}`,
          location: 'Saturn'
        };
      });
      const header = [
        { filter: textFilter(), dataField: 'username', text: 'username', sort: true},
        { dataField: 'realname', text: 'realname', sort: true },
        { dataField: 'location', text: 'location' }
      ];

      useEffect(() => {
        axios({
            method: 'post',
            url: 'http://localhost/KV/bankovnisustav/src/PHP/ReadWrite.php',
            data: {
                RequestId: 'Ucitaj_podatke_klijenti',
            },
            headers: { 
                "Content-Type": "multipart/form-data",
            } ,
        }).then(function (response) {
            //handle success
            console.log(response.data);
            setData(response.data);
          }).catch(function (response) {
            //handle error
            console.log(response);
          });
        }, []);

        if(data){
        return(
            <div className="container">
                <BootstrapTable keyField='id' data={ body } columns={ header } pagination={ paginationFactory() } filter={ filterFactory() } />
            </div>
        );
        }
    
}

export default Klijent;
