import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import React from "react";
import { Line } from "react-chartjs-2";
import axios from 'axios';

function OnlineRacun(){
    const [data, setData] = useState(null);
    var transakcije;
    const { IdRacun } = useParams();
    var dates;
    var values;

    useEffect(() => {
        UcitajRacune();
        
    },[]);

    async function UcitajRacune(){
        axios({
            method: 'post',
            url: 'http://localhost/KV/bankovnisustav/src/PHP/ReadWrite.php',
            data: {
                RequestId: 'Ucitaj_racun',
                Sifra: IdRacun
            },
            headers: { 
                "Content-Type": "multipart/form-data",
            } ,
        }).then(function (response) {
            //handle success
            console.log(response.data);
            setData(response.data);
            dates= data.loTransakcije.map((x)=>x.Datum);
            values= data.loTransakcije.map((x)=>x.TrenutnoStanje);
          }).catch(function (response) {
            //handle error
            console.log(response);
          });
    }
    if(!data) return;
    
    return(
        <div className="container">
            <h3 className="mt-5">Line chart</h3>
            <Line data={this.state.dataLine} options={{ responsive: true }} />
        </div>
    );
}


export default OnlineRacun;
