import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import React from "react";
import { Line } from "react-chartjs-2";
import axios from 'axios';

function OnlineRacun(){
    const [data, setData] = useState(null);
    var transakcije;
    const { IdRacun } = useParams();

    useEffect(() => {
        UcitajRacune();
        
    },[]);

    async function UcitajRacune(){
        axios({
            method: 'post',
            url: 'http://localhost/KV/bankovnisustav/src/PHP/ReadWrite.php',
            data: {
                RequestId: 'Ucitaj_racun',
                Sifra: IdRacun
            },
            headers: { 
                "Content-Type": "multipart/form-data",
            } ,
        }).then(function (response) {
            //handle success
            console.log(response.data);
            setData(response.data);
            transakcije= data.loTransakcije;
          }).catch(function (response) {
            //handle error
            console.log(response);
          });
    }
    if(!data) return;
    
    
    
    var dates= transakcije.map((x)=>x.Datum);
    var values= transakcije.map((x)=>x.TrenutnoStanje);
    
    class ChartsPage extends React.Component {
        state = {
          dataLine: {
            labels: dates,
            datasets: [
              {
                label: "My First dataset",
                fill: true,
                lineTension: 0.3,
                backgroundColor: "rgba(225, 204,230, .3)",
                borderColor: "rgb(205, 130, 158)",
                borderCapStyle: "butt",
                borderDash: [],
                borderDashOffset: 0.0,
                borderJoinStyle: "miter",
                pointBorderColor: "rgb(205, 130,1 58)",
                pointBackgroundColor: "rgb(255, 255, 255)",
                pointBorderWidth: 10,
                pointHoverRadius: 5,
                pointHoverBackgroundColor: "rgb(0, 0, 0)",
                pointHoverBorderColor: "rgba(220, 220, 220,1)",
                pointHoverBorderWidth: 2,
                pointRadius: 1,
                pointHitRadius: 10,
                data: values
              }]
            }
        }
    }
    return(
        <div className="container"></div>
    );
}

export default OnlineRacun;
