import { useEffect, useState } from "react";
import axios from 'axios';
import {
    DatatableWrapper,
    Filter,
    Pagination,
    PaginationOptions,
    TableBody,
    TableHeader
  } from 'react-bs-datatable';
  import { Col, Row, Table } from 'react-bootstrap';

function Klijent(){

    const [data, setData] = useState(null);

    /*const header = [
        { title: 'OIB', prop: 'OIB' },
        { title: 'Ime', prop: 'Ime' },
        { title: 'Prezime', prop: 'Prezime' },
        { title: 'Adresa', prop: 'Adresa' },
        { title: 'Telefon', prop: 'Telefon' },
        { title: 'Spol', prop: 'Spol' },
      ];*/
      const body = Array.from(new Array(57), () => {
        const rd = (Math.random() * 10).toFixed(1);
      
        if (rd > 0.5) {
          return {
            username: 'i-am-billy',
            realname: `Billy ${rd}`,
            location: 'Mars'
          };
        }
      
        return {
          username: 'john-nhoj',
          realname: `John ${rd}`,
          location: 'Saturn'
        };
      });
      const header = [
        { title: 'Username', prop: 'username' },
        { title: 'Name', prop: 'realname' },
        { title: 'Location', prop: 'location' }
      ];

      useEffect(() => {
        axios({
            method: 'post',
            url: 'http://localhost/KV/bankovnisustav/src/PHP/ReadWrite.php',
            data: {
                RequestId: 'Ucitaj_podatke_klijenti',
            },
            headers: { 
                "Content-Type": "multipart/form-data",
            } ,
        }).then(function (response) {
            //handle success
            console.log(response.data);
            setData(response.data);
          }).catch(function (response) {
            //handle error
            console.log(response);
          });
        }, []);

        if(data){
        return(
            <DatatableWrapper body={body} headers={header}>
                <Table>
                    <TableHeader />
                    <TableBody />
                </Table>
                <Row className="mb-4">
                    <Col
                      xs={12}
                      lg={4}
                      className="d-flex flex-col justify-content-end align-items-end"
                    >
                      <Filter />
                    </Col>
                    <Col
                      xs={12}
                      sm={6}
                      lg={4}
                      className="d-flex flex-col justify-content-lg-start align-items-center justify-content-sm-start mb-2 mb-sm-0"
                    >
                      <PaginationOptions />
                    </Col>
                    <Col
                      xs={12}
                      sm={6}
                      lg={4}
                      className="d-flex flex-col justify-content-end align-items-end"
                    >
                      <Pagination />
                    </Col>
                </Row>
            </DatatableWrapper>

        );
        }
    
}

export default Klijent;
