            />
            <label>Opis:</label>
            <input
            className="form-control mb-3"
            type="text"
            name="Opis"
            value={inputs.Opis||""}
            onChange={handleChange}
            /> 
        </Modal.Body>
        <Modal.Footer>
            <Button variant="secondary" onClick={modalTansactionClose}>Close</Button>
            <Button variant="primary" onClick={""}>IzvrÅ¡i transakciju</Button>
        </Modal.Footer>
    </Modal >
    </div>
    );
}

